import React from 'react'
import styled from 'styled-components'
import Button from './Button';

import img1 from '../assets/Nfts/bighead.svg'
import img2 from '../assets/Nfts/bighead-1.svg'
import img3 from '../assets/Nfts/bighead-2.svg'
import img4 from '../assets/Nfts/bighead-3.svg'
import img5 from '../assets/Nfts/bighead-4.svg'
import img6 from '../assets/Nfts/bighead-5.svg'

const Section = styled.section`
  width: 100vw;
  height: 25rem;
  position: relative;
  border-top: 2px solid ${props => props.theme.text};
  border-top: 2px solid ${props => props.theme.text};

  background-color: ${props => `rgba(${props.theme.textRgba}, 0.9)`};

  display: flex;
  justify-content: center;
  align-items: center;

  overflow: hidden;
`

const ImageContainer = styled.div`
  width: 100%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  display: flex;
  justify-content: center;
  align-items: center;
  opacity: 0.2;

  img{
    width: 15rem;
    height: auto;
  }
`

const Title = styled.h1`
  font-size: ${props => props.theme.fontxxxl};
  color: ${props => props.theme.body};
  padding: 1rem 2rem;
  z-index: 10;
  width: 35%;
`

const ButtonContainer = styled.div`
  width: 35%;
  display: flex;
  justify-content: flex-end;
`

const ButtonJoinNow = styled.button`
  display: inline-block;
  background-color: ${props => props.theme.text};
  color: ${props => props.theme.body};
  outline: none;
  border: none;

  font-size: ${props => props.theme.fontsm};
  padding: 0.8rem 2rem;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.2s ease;
  position: relative;

  &:hover{
    transform: scale(0.9);
  }

  &::after{
    content: ' ';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) scale(0);
    border: 2px solid ${props => props.theme.text};
    width: 100%;
    height: 100%;
    border-radius: 50px;
    transition: all 0.1s ease;
  }

  &:hover::after{
    transform: translate(-50%, -50%) scale(1);
    padding: 0.3rem;
  }  
`

const Banner = () => {
  return (
    <Section>
      <ImageContainer>
        <img src={img1} alt='Weirdos' />
        <img src={img2} alt='Weirdos' />
        <img src={img3} alt='Weirdos' />
        <img src={img4} alt='Weirdos' />
        <img src={img5} alt='Weirdos' />
        <img src={img6} alt='Weirdos' />
      </ImageContainer>
      <Title>
        Join the <br /> weirdos club
      </Title>
      <ButtonContainer>
        <ButtonJoinNow>

        </ButtonJoinNow>
        <Button text="join now" link="#" />
      </ButtonContainer>
    </Section>
  )
}

export default Banner
